import styled from 'styled-components';

export const Container = styled.div`
background: #fff;
padding:30px;
border-radius:4px;
`;

export const ProductTable = styled.table`
width:100%;

thead th{
  color:#999;
  text-align:left;
  padding:12px;
}
tbody{
  padding:12px;
  border-bottom: 1px solid #eee;
}
img{
  height: 100px;

}
strong{
  color:#333;
  display:block;
}
span{
  display:block;
  margin-top:5px;
  font-size:18px;
  font-weight:bold;

}
div{
  display: flex;
  align-items:center;
}

`;

export const Total = styled.div``;
