import styled from 'styled-components';


export const ProductList = styled.ul`
display:grid;
grid-template-columns:repeat(3,1fr);
grid-gap: 20px;
list-style:none;
li{
  display:flex;
  flex-direction:column;
  background:#fff;
  border-radius: 4px;
  padding: 20px;


}
img{
  align-self:center;
}
`
