import styled from 'styled-components';
import { darken } from 'polished';


export const ProductList = styled.ul`
display:grid;
grid-template-columns:repeat(3,1fr);
grid-gap: 20px;
list-style:none;
li{
  display:flex;
  flex-direction:column;
  background:#fff;
  border-radius: 4px;
  padding: 20px;

  img{
    align-self:center;
    max-width: 250px;
  }
  > strong {
    font-size:16px;
    line-height: 20px;
  }
  > span{
    color:#eee;
    font-size: 21px;
    font-weight:bold;
    margin: 5px 0 20px;

  }

  button{
    background:#7159c1;
    color: #fff;
    border: 0 ;
    border-radius: 4px;
    overflow:hidden;
    margin-top:auto;
    display:flex;
    align-items:center;
    transition: 0.15s;
    &:hover{
      background: ${darken(0.05,'#7159c1')}
    }
    >span{
    flex: 1;
    text-align:center;
    font-weight: bold;
  }

  }
  div{

    display:flex;
    align-items:center;
    padding:10px;
    background: rgba(0,0,0,0.2);
  }
  svg{
    margin-right:5px;
  }


}

`
